mapper.select:
select count(*) from online_number_2018_10


        date like CONCAT('%',#{date},'%')



        and  officialNum > #{officialNum}  


        and  officialNum < -#{officialNum}  



        and  mixNum > #{mixNum}  


        and  mixNum < -#{mixNum}  



        and  gatNum > #{gatNum}  


        and  gatNum < -#{gatNum}  



        and  totalNum > #{totalNum}  


        and  totalNum < -#{totalNum}

select date,officialNum,mixNum,gatNum,totalNum from online_number_2018_10


        date like CONCAT('%',#{date},'%')



        and  officialNum > #{officialNum}  


        and  officialNum < -#{officialNum}  



        and  mixNum > #{mixNum}  


        and  mixNum < -#{mixNum}  



        and  gatNum > #{gatNum}  


        and  gatNum < -#{gatNum}  



        and  totalNum > #{totalNum}  


        and  totalNum < -#{totalNum}  



order by id desc


    limit #{page.startRow},#{page.pageSize}
